import aboutpic from "../images/interior/aboutpic.jpeg";

export default function About() {
  return (
    <>
      <div>
        <img src={aboutpic} />
        <div
          style={{
            position: "absolute",
            top: "40%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            color: "white",
            fontSize: "24px",
            fontWeight: "bold",
            textShadow: "2px 2px 4px rgba(0, 0, 0, 0.8)",
          }}
        >
          About Us
        </div>
      </div>
    </>
  );
}
